// THE CHART
Highcharts.stockChart('container', {
    chart: {
        type: 'line'
    },
    title: {
        text: 'Grid axis with navigator'
    },

    subtitle: {
        text: 'Using the time navigation features from Highcharts Stock'
    },

    xAxis: [{
        id: 'bottom-datetime-axis',
        grid: {
            enabled: true
        },
        opposite: true,
        type: 'datetime'
    }, {
        grid: {
            enabled: true
        },
        type: 'datetime',
        opposite: true,
        labels: {
            style: {
                fontSize: '15px'
            }
        },
        linkedTo: 0
    }],
    series: [{
        name: 'Project 1',
        xAxis: 0,
        data: usdeur
    }]
});

npm:
// 安装
npm install highcharts@10.3.2 --save

// import 模块 
import Highcharts from 'highcharts/highstock';
import GridAxis   from 'highcharts/modules/grid-axis';
import Usdeur     from 'https://www.highcharts.com/samples/data/usdeur.js';
import Zh_cn      from 'https://code.hcharts.cn/plugins/zh_cn.js';

// 初始化模块
GridAxis(Highcharts);
Usdeur(Highcharts);
Zh_cn(Highcharts);
