Highcharts.chart('container', {
    chart: {
        type: 'pie',
        options3d: {
            enabled: true,
            alpha: 15,
            beta: 15
        }
    },
    plotOptions: {
        pie: {
            allowPointSelect: true,
            slicedOffset: 25,
            depth: 25
        }
    },
    series: [{
        data: [2, 4, 6, 1, 3]
    }]
});

