npm:
// 安装
npm install highcharts@10.3.2 --save

// import 模块 
import Highcharts        from 'highcharts';
import Accessibility     from 'highcharts/modules/accessibility';
import PatternFill       from 'highcharts/modules/pattern-fill';
import HighContrastLight from 'highcharts/themes/high-contrast-light';
import HighchartsZh_CN   from 'https://code.hcharts.cn/plugins/highcharts-zh_CN.js';

// 初始化模块
Accessibility(Highcharts);
PatternFill(Highcharts);
HighContrastLight(Highcharts);
HighchartsZh_CN(Highcharts);
