                        });

                        var mq = window.matchMedia('(max-width: 480px)');
                        var caption = getEl('dyncaption');
                        var label = getEl(category);
                        if (mq.matches) {
                            caption.className = 'visible content-' + category;
                            getEl('caption-content').innerHTML = label.innerHTML;
                        } else {
