body {
    margin: 0;
}

.visually-hidden {
    width: 1px;
    height: 1px;
    position: absolute;
    overflow: hidden;
    white-space: nowrap;
    clip: rect(1px, 1px, 1px, 1px);
    margin-top: -3px;
    filter: alpha(opacity=1);
    opacity: 1%;
}

.btn {
    font-family: "IBM Plex Sans", sans-serif;
    white-space: normal;
    background-color: #5749ad;
    border: 1px solid #5749ad;
    cursor: pointer;
    color: #fff;
    min-height: 90px;
    max-width: 200px;
    width: 100%;
    border-radius: 4px;
    font-size: 1.1rem;
    font-weight: 700;
    text-align: center;
    padding: 0.375rem 0.75rem;
    margin: 0.25rem 35% 0.25rem 35;
    box-shadow: 0 0 5px rgb(0 0 0 / 40%);
    transition: font-size 0.15s;
}

.btn:hover,
.btn:focus {
    filter: brightness(80%);
    font-size: 1.12rem;
}

#outer {
    min-width: 360px;
    max-width: 800px;
    margin: 0 auto;
    position: relative;
}

#overlay {
    display: flex;
    align-items: center;
    justify-content: center;
    position: absolute;
    margin-top: 5px;
    margin-bottom: 5px;
    top: 0;
    bottom: 0;
    right: 0;
    left: 0;
    z-index: 999;
}

.highcharts-figure {
    max-width: 600px;
    margin: 0 auto;
}

#container {
    width: 100%;
    height: 450px;
    margin-bottom: -20px;
    margin-top: -20px;
}

.caption-arrow {
    width: 0;
    height: 0;
    border-left: 8px solid transparent;
    border-right: 8px solid transparent;
    border-bottom: 8px solid #eee;
    position: absolute;
    top: -8px;
    left: calc(50% - 4px);
}

#dyncaption {
    display: none;
    position: relative;
    font-family: Roboto, sans-serif;
    margin: 0;
    border: 2px solid #eee;
    border-left: none;
    border-right: none;
    border-radius: 3px;
