Highcharts.chart('container', {
    title: {
        text: 'Chart with Annotations GUI'
    },
    subtitle: {
        text: 'Use the dropdown to add or edit annotations'
    },
    navigation: {
        events: {
            selectButton: function (event) {
                var newClassName = event.button.className + ' highcharts-active',
                    topButton = event.button.parentNode.parentNode;

                if (topButton.classList.contains('right')) {
                    newClassName += ' right';
                }

                // If this is a button with sub buttons,
                // change main icon to the current one:
                if (!topButton.classList.contains('highcharts-menu-wrapper')) {
                    topButton.className = newClassName;
                }

                // Store info about active button:
                this.chart.activeButton = event.button;
            },
            deselectButton: function (event) {
                event.button.parentNode.parentNode.classList.remove('highcharts-active');

                // Remove info about active button:
                this.chart.activeButton = null;
            }
        }
    },

    series: [{
        data: [7.0, 6.9, 9.5, 14.5, 18.2, 21.5, 25.2, 23.3, 18.3, 13.9, 9.6]
    }]
});

css:
/* FULL SCREEN */
.chart:-webkit-full-screen {
    width: 100%;
    height: 100%;
}

.chart:-moz-full-screen {
    width: 100%;
    height: 100%;
}

.chart:-ms-fullscreen {
    width: 100%;
    height: 100%;
}

.chart:fullscreen {
    width: 100%;
    height: 100%;
}

/* GENERAL */
.chart {
    float: left;
    height: 400px;
    position: relative;
    width: 100%;
}
.highcharts-draw-mode { cursor: crosshair; }
.left { float: left; }

.right,
.highcharts-dropdown li.right { float: right; }

/* GUI */
.highcharts-dropdown {
    margin: 0 0 0 10px;
    padding: 0;
    width: calc(100% - 63px);
}

.highcharts-dropdown li {
    background-color: #f7f7f7;
    background-repeat: no-repeat;
    cursor: pointer;
    float: left;
    height: 40px;
    list-style: none;
    margin-right: 2px;
    margin-bottom: 3px;
    padding: 0;
    position: relative;
    width: auto;
}

.highcharts-dropdown li ul {
    display: none;
    left: 0;
    padding-left: 0;
    position: absolute;
    z-index: 125;
}
.highcharts-dropdown li:hover { background-color: #e6ebf5; }
.highcharts-dropdown li:hover ul { display: block; }

.highcharts-dropdown li ul li {
    margin-bottom: 0;
    width: 160px;
}

.highcharts-dropdown li > span.highcharts-menu-item-btn {
    background-repeat: no-repeat;
    background-position: 50% 50%;
    display: block;
    float: left;
    height: 100%;
    width: 40px;
}

.highcharts-dropdown li > .highcharts-menu-item-title {
    color: #666;
    line-height: 40px;
    font-size: 0.876em;
    padding: 0 10px 0 5px;
}

.highcharts-label-annotation > .highcharts-menu-item-btn { background-image: url("https://code.highcharts.com/gfx/stock-icons/label.svg"); }
.highcharts-circle-annotation > .highcharts-menu-item-btn { background-image: url("https://code.highcharts.com/gfx/stock-icons/circle.svg"); }
.highcharts-rectangle-annotation > .highcharts-menu-item-btn { background-image: url("https://code.highcharts.com/gfx/stock-icons/rectangle.svg"); }
.highcharts-ellipse-annotation > .highcharts-menu-item-btn { background-image: url("https://code.highcharts.com/gfx/stock-icons/ellipse.svg"); }

li.highcharts-active { background-color: #e6ebf5; }

/* Popup */
.chart-wrapper {
    float: left;
    position: relative;
    width: 100%;
}

/* Responsive design */
@media screen and (max-width: 1024px) {
    .highcharts-dropdown li > .highcharts-menu-item-title { display: none; }
    .highcharts-dropdown li ul li { width: auto; }
}

