js:
Highcharts.chart('container', {
    chart: {
        type: 'column'
    },
    title: {
        text: 'Color blind demo using pattern fills'
    },
    xAxis: {
        categories: ['A', 'B']
    },
    credits: {
        enabled: false
    },
    series: [{
        name: 'V1',
        color: 'url(#highcharts-default-pattern-0)',
        data: [5, 3]
    }, {
        name: 'V2',
        color: 'url(#highcharts-default-pattern-8)',
        data: [2, -2]
    }, {
        name: 'V3',
        data: [3, 4]
    }]
});

npm:
// 安装
npm install highcharts@11.0.0 --save

// import 模块 
import Highcharts    from 'highcharts';
import Exporting     from 'highcharts/modules/exporting';
import PatternFillV2 from 'https://highcharts.github.io/pattern-fill/pattern-fill-v2.js';

// 初始化模块
Exporting(Highcharts);
PatternFillV2(Highcharts);
