js:
Highcharts.chart("container", {
    data: {
        table: "datatable"
    },
    chart: {
        type: "column"
    },
    title: {
        text: "Vitamins purchased by men vs. women"
    },
    subtitle: {
        text:
      'Source:<a href="http://www.roymorgan.com/findings/7956-australian-vitamin-market-december-2018-201904260734" target="_blank">Roy Morgan</a>'
    },
    plotOptions: {
        series: {
            dataLabels: {
                enabled: true,
                format: "{y} %"
            }
        }
    },
    yAxis: {
        labels: { format: "{text}%" },

        title: {
            text: null
        }
    },
    colors: ["#058DC7", "#c7058d"],
    tooltip: {
        valueSuffix: " %"
    }
});

css:
#container {
    height: 400px;
}

.highcharts-figure,
.highcharts-data-table table {
    min-width: 310px;
    max-width: 800px;
    margin: 1em auto;
}

#datatable {
    font-family: Verdana, sans-serif;
    border-collapse: collapse;
    border: 1px solid #ebebeb;
    margin: 10px auto;
    text-align: center;
    width: 100%;
    max-width: 500px;
}

#datatable caption {
    padding: 1em 0;
    font-size: 1.2em;
    color: #555;
}

#datatable th {
    font-weight: 600;
    padding: 0.5em;
}

#datatable td,
#datatable th,
#datatable caption {
    padding: 0.5em;
}

#datatable thead tr,
#datatable tr:nth-child(even) {
    background: #f8f8f8;
}

#datatable tr:hover {
    background: #f1f7ff;
}

