];

Highcharts.chart('container', {
    chart: {
        type: 'scatter',
        zoomType: 'x'
    },
    title: {
        text: 'Sound pressure level'
    },
    xAxis: {
        minRange: 1,
        title: {
            enabled: true,
            text: 'Distance (m)'
        }
    },
    yAxis: {
        title: {
            text: 'SPL (dB)'
        }
    },
    tooltip: {
        pointFormat: '{point.series.name}: {point.y:.2f}'
    },
    plotOptions: {
        scatter: {
            marker: {
                radius: 5,
                states: {
                    hover: {
                        enabled: true,
                        lineColor: 'rgb(100,100,100)'
                    }
                }
            },
            states: {
                hover: {
                    marker: {
                        enabled: false
                    }
                }
            }
        }
    },
    series: [{
        regression: true,
        regressionSettings: {
            type: 'logarithmic',
            color: 'rgba(23, 83, 183, .9)'
        },
        name: 'measurement 1',
        color: 'rgba(23, 83, 183, 1)',
        data: data1
    }, {
        regression: true,
        regressionSettings: {
            type: 'logarithmic',
            color: 'rgba(20, 183, 83, .9)'
        },
        name: 'measurement 2',
        color: 'rgba(20, 183, 83, 1)',
        data: data2
    }, {
        type: 'column',
        regression: true,
        regressionSettings: {
            type: 'logarithmic',
            color: 'rgba(203, 83, 183, .9)'
        },
        name: 'measurement 3',
        color: 'rgba(203, 83, 183, .5)',
        data: data3
    }]
}, function (chart) {
    document.querySelector('.change').addEventListener('click', function () {
        var seriesInd = parseInt(document.querySelector('.series').value, 10) - 1;
        var type = chart.series[seriesInd].type === 'column' ? 'scatter' : 'column';
        chart.series[seriesInd].update({
            type: type
        });
    });
});

