const chart = Highcharts.chart('chart1', {
    chart: {
        reflow: false
    },
    title: {
        text: 'Click chart to view full page'
    },
    xAxis: {
        categories: ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun',
            'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec']
    },
    series: [{
        data: [29.9, 71.5, 106.4, 129.2, 144.0, 176.0, 135.6, 148.5, 216.4,
            194.1, 95.6, 54.4]
    }]
});

document.querySelector('.chart-container').addEventListener('click', e => {
    e.currentTarget.classList.toggle('modal');
    chart.reflow();
});

css:
.main {
    width: 400px;
    margin: 0 auto;
}

.chart {
    min-width: 300px;
    max-width: 800px;
    height: 300px;
    margin: 1em auto;
}

.modal {
    position: fixed;
    width: 100%;
    height: 100%;
    top: 0;
    left: 0;
    background: rgb(0 0 0 / 70%);
}

.modal .chart {
    height: 90%;
    width: 90%;
    max-width: none;
}

