js:
Highcharts.setOptions({
    colors: ["#71BF45", "#FAA74B", "#01BAF2", "#f23901", "#A0A0A0"]
});

Highcharts.chart("container", {
    chart: {
        type: "bar"
    },
    title: {
        text: "Honey Composition"
    },
    subtitle: {
        text: 'Source: <a href="https://en.wikipedia.org/wiki/Honey">Wikipedia</a>'
    },
    xAxis: {
        categories: ["Honey composition"],
        visible: false
    },
    yAxis: {
        labels: {
            enabled: false
        },
        visible: false,
        //reversed: true,
        min: 0,
        title: {
            text: null
        }
    },
    tooltip: {
        pointFormat:
      '<span style="color:{series.color}">{series.name}</span>: <b>{point.percentage:.0f}%</b><br/>',
        shared: true
    },
    plotOptions: {
        bar: {
            stacking: "percent",
            dataLabels: {
                enabled: true,
                format: "{series.name}: {y}%",
                color: "black",
                rotation: -55,
                y: -100,
                x: 30
            }
        }
    },
    series: [
        {
            name: "Fructose",
            data: [38.2]
        },
        {
            name: "Glucose",
            data: [31.3]
        },
        {
            name: "Water",
            data: [17.2]
        },
        {
            name: "Maltose",
            data: [7.1]
        },
        { name: "Other", data: [6.2] }
    ]
});

css:
#container: {
    min-width: 310px;
    height: 400px;
    margin: 0 auto;
}

.highcharts-figure,
.highcharts-data-table table {
    min-width: 320px;
    max-width: 800px;
    margin: 1em auto;
}

.highcharts-data-table table {
    font-family: Verdana, sans-serif;
    border-collapse: collapse;
    border: 1px solid #ebebeb;
    margin: 10px auto;
    text-align: center;
    width: 100%;
    max-width: 500px;
}

.highcharts-data-table caption {
    padding: 1em 0;
    font-size: 1.2em;
    color: #555;
}

.highcharts-data-table th {
    font-weight: 600;
    padding: 0.5em;
}

.highcharts-data-table td,
.highcharts-data-table th,
.highcharts-data-table caption {
    padding: 0.5em;
}

.highcharts-data-table thead tr,
.highcharts-data-table tr:nth-child(even) {
    background: #f8f8f8;
}

.highcharts-data-table tr:hover {
    background: #f1f7ff;
}

