js:
Highcharts.chart('container', {
    chart: {
        type: 'streamgraph',
        marginBottom: 30,
        zoomType: 'x'
    },
    title: {
        floating: true,
        align: 'left',
        text: 'JS Frameworks Trends'
    },
    subtitle: {
        floating: true,
        align: 'left',
        y: 30,
        text:
        'Source: <a href="https://trends.google.com/trends/" target="_blank">Google trends</a>'
    },

    xAxis: {
        maxPadding: 0,
        crosshair: true,
        labels: {
            align: 'left',
            reserveSpace: false,
            rotation: 270
        },
        lineWidth: 0,
        margin: 20,
        tickWidth: 0
    },

    yAxis: {
        visible: false,
        startOnTick: false,
        endOnTick: false
    },

    legend: {
        enabled: false
    },

    plotOptions: {
        series: {
            label: {
                minFontSize: 5,
                maxFontSize: 15,
                style: {
                    color: 'rgba(255,255,255,0.75)'
                }
            }
        }
    },
    tooltip: {
        xDateFormat: '%B %Y',
        shared: true
    },

    data: {
        csvURL: 'https://cdn.jsdelivr.net/gh/highcharts/highcharts@24912efc85/samples/data/js-frameworks-trends.csv',
        endColumn: 4
    },

    series: [
        { color: '#4284F3' },
        { color: '#33A852' },
        { color: '#FABD03' },
        { color: '#EA4435' }
    ]
});

css:
#container {
    height: 600px;
}

.highcharts-figure,
.highcharts-data-table table {
    min-width: 310px;
    max-width: 800px;
    overflow: auto;
    margin: 1em auto;
}

.highcharts-data-table table {
    font-family: Verdana, sans-serif;
    border-collapse: collapse;
    border: 1px solid #ebebeb;
    margin: 10px auto;
    text-align: center;
    width: 100%;
    max-width: 500px;
}

.highcharts-data-table caption {
    padding: 1em 0;
    font-size: 1.2em;
    color: #555;
}

.highcharts-data-table th {
    font-weight: 600;
    padding: 0.5em;
}

.highcharts-data-table td,
.highcharts-data-table th,
.highcharts-data-table caption {
    padding: 0.5em;
}

.highcharts-data-table thead tr,
.highcharts-data-table tr:nth-child(even) {
    background: #f8f8f8;
}

.highcharts-data-table tr:hover {
    background: #f1f7ff;
}

npm:
// 安装
npm install highcharts@11.0.0 --save

// import 模块 
import Highcharts    from 'highcharts';
import Data          from 'highcharts/modules/data';
import Streamgraph   from 'highcharts/modules/streamgraph';
import SeriesLabel   from 'highcharts/modules/series-label';
import Annotations   from 'highcharts/modules/annotations';
import Exporting     from 'highcharts/modules/exporting';
import ExportData    from 'highcharts/modules/export-data';
import Accessibility from 'highcharts/modules/accessibility';

// 初始化模块
Data(Highcharts);
Streamgraph(Highcharts);
SeriesLabel(Highcharts);
Annotations(Highcharts);
Exporting(Highcharts);
ExportData(Highcharts);
Accessibility(Highcharts);
