js:
Highcharts.chart("container", {
    title: {
        text: "Bergen Wind Forecast"
    },
    subtitle: {
        text:
        'Source: <a href="https://www.yr.no/en/details/graph/1-92416/Norway/Vestland/Bergen/Bergen">Norwegian Meteorological Institute</a>'
    },

    xAxis: {
        crosshair: true,
        type: "datetime"
    },

    yAxis: {
        title: {
            text: null
        },
        labels: {
            format: "{text}m/s"
        }
    },

    tooltip: {
        crosshairs: true,
        shared: true,
        valueSuffix: "m/s",
        xDateFormat: "%A, %b %e"
    },

    data: {
        csvURL:
        "https://cdn.jsdelivr.net/gh/highcharts/highcharts@24912efc85/samples/data/bergen-wind-prediction.csv",
        seriesMapping: [{ low: 0, high: 1 }, { y: 2 }],
        complete: function (options) {
            options.series[1].name = "Possible wind";
        }
    },

    plotOptions: {
        series: {
            label: {
                connectorAllowed: false
            },
            marker: {
                enabled: false
            },
            pointStart: Date.UTC(2022, 3, 3, 15),
            pointInterval: 36e5 // one hour
        }
    },

    series: [
        {
            type: "areasplinerange",
            color: {
                patternIndex: 0
            }
        },
        {
            type: "spline",
            lineWidth: 3,
            color: "#AA00F2"
        }
    ]
});

css:
#container {
    height: 600px;
}

.highcharts-figure,
.highcharts-data-table table {
    min-width: 310px;
    max-width: 800px;
    overflow: auto;
    margin: 1em auto;
}

.highcharts-data-table table {
    font-family: Verdana, sans-serif;
    border-collapse: collapse;
    border: 1px solid #ebebeb;
    margin: 10px auto;
    text-align: center;
    width: 100%;
    max-width: 500px;
}

.highcharts-data-table caption {
    padding: 1em 0;
    font-size: 1.2em;
    color: #555;
}

.highcharts-data-table th {
    font-weight: 600;
    padding: 0.5em;
}

.highcharts-data-table td,
.highcharts-data-table th,
.highcharts-data-table caption {
    padding: 0.5em;
}

.highcharts-data-table thead tr,
.highcharts-data-table tr:nth-child(even) {
    background: #f8f8f8;
}

.highcharts-data-table tr:hover {
    background: #f1f7ff;
}

