css:
@import "https://code.highcharts.com/css/highcharts.css";

#container {
    height: 400px;
    max-width: 800px;
    min-width: 320px;
    margin: 0 auto;
}

.highcharts-color-0 {
    fill: green;
    stroke: green;
}

.highcharts-color-1 {
    fill: orange;
    stroke: orange;
}

.highcharts-color-2 {
    fill: red;
    stroke: red;
}

/* Reset side-effect of setting colored fill */
.highcharts-data-label-connector {
    fill: none !important;
}

