js:
Highcharts.getJSON(
    'https://demo-live-data.highcharts.com/aapl-c.json',
    function (data) {
        // Create the chart
        Highcharts.stockChart('container', {

            chart: {
                marginRight: 50,
                styledMode: true
            },

            rangeSelector: {
                selected: 1
            },

            title: {
                text: 'AAPL Stock Price'
            },

            xAxis: {
                crosshair: true
            },

            yAxis: {
                opposite: true,
                crosshair: {
                    label: {
                        enabled: true,
                        format: '{value:.2f}'
                    }
                },
                labels: {
                    align: 'left',
                    format: '{value:.2f}',
                    y: 6,
                    x: 2
                }
            },

            series: [{
                name: 'AAPL',
                data: data,
                tooltip: {
                    valueDecimals: 2
                }
            }]
        });
    }
);

css:
@import "https://code.highcharts.com/css/highcharts.css";

#container {
    height: 400px;
    max-width: 800px;
    margin: 0 auto;
}

.highcharts-crosshair-label {
    stroke-width: 1px;
    stroke: gray;
}

.highcharts-crosshair-label text {
    fill: black;
}

