js:
Highcharts.chart('container', {

    chart: {
        type: 'gauge',
        styledMode: true
    },

    title: {
        text: 'Speedometer'
    },

    pane: {
        startAngle: -150,
        endAngle: 150,
        background: [{
            className: 'outer-pane',
            outerRadius: '115%'
        }, {
            className: 'middle-pane',
            outerRadius: '112%'
        }, {
            // default background
        }, {
            className: 'inner-pane',
            outerRadius: '105%',
            innerRadius: '103%'
        }]
    },

    // the value axis
    yAxis: {
        min: 0,
        max: 200,

        minorTickInterval: 'auto',
        minorTickLength: 10,
        minorTickPosition: 'inside',

        tickPixelInterval: 30,
        tickPosition: 'inside',
        tickLength: 10,
        labels: {
            step: 2,
            rotation: 'auto'
        },
        title: {
            text: 'km/h'
        },
        plotBands: [{
            from: 0,
            to: 120,
            className: 'green-band'
        }, {
            from: 120,
            to: 160,
            className: 'yellow-band'
        }, {
            from: 160,
            to: 200,
            className: 'red-band'
        }]
    },

    series: [{
        name: 'Speed',
        data: [80],
        tooltip: {
            valueSuffix: ' km/h'
        }
    }]

},
// Add some life
function (chart) {
    if (!chart.renderer.forExport) {
        setInterval(function () {
            var point = chart.series[0].points[0],
                newVal,
                inc = Math.round((Math.random() - 0.5) * 20);

            newVal = point.y + inc;
            if (newVal < 0 || newVal > 200) {
                newVal = point.y - inc;
            }

            point.update(newVal);

        }, 3000);
    }
});

css:
@import "https://code.highcharts.com/css/highcharts.css";

#container {
    min-width: 310px;
    max-width: 400px;
    height: 300px;
    margin: 0 auto;
}

.outer-pane {
    fill: #efefef;
}

.middle-pane {
    stroke-width: 1px;
    stroke: #aaa;
}

.inner-pane {
    fill: #ddd;
}

.green-band {
    fill: #55bf3b;
    fill-opacity: 1;
}

.yellow-band {
    fill: #dddf0d;
    fill-opacity: 1;
}

.red-band {
    fill: #df5353;
    fill-opacity: 1;
}

.highcharts-gauge-series .highcharts-pivot {
    stroke-width: 1px;
    stroke: black;
    fill-opacity: 0.3;
}

.highcharts-gauge-series .highcharts-dial {
    fill: gray;
}

.highcharts-yaxis .highcharts-tick {
    stroke-width: 2px;
    stroke: #666;
}

.highcharts-yaxis-grid .highcharts-grid-line,
.highcharts-yaxis-grid .highcharts-minor-grid-line {
    display: none;
}

.highcharts-yaxis .highcharts-minor-tick {
    stroke-width: 1px;
    stroke: #999;
}

