@import "https://code.highcharts.com/css/highcharts.css";

#container {
    height: 500px;
    min-width: 310px;
    max-width: 800px;
    margin: 0 auto;
}

.loading {
    margin-top: 10em;
    text-align: center;
    color: gray;
}

.highcharts-legend .highcharts-point {
    stroke-width: 0;
}

/* Data class colors */
.highcharts-color-0 {
    fill: rgb(239 239 255);
    stroke: rgb(239 239 255);
}

.highcharts-color-1 {
    fill: rgb(120 148 186);
    stroke: rgb(120 148 186);
}

.highcharts-color-2 {
    fill: rgb(0 56 117);
    stroke: rgb(0 56 117);
}

