const chart = Highcharts.chart('container', {
    chart: {
        styledMode: true
    },
    title: {
        text: 'Tooltip styled by custom CSS'
    },
    yAxis: {
        title: {
            text: null
        }
    },
    series: [{
        type: 'line',
        name: 'Apples',
        className: 'apples',
        data: [
            0,
            -1,
            3,
            1,
            0]
    }, {
        type: 'line',
        name: 'Oranges',
        className: 'oranges',
        data: [
            1,
            3,
            {
                className: 'blood-orange',
                y: 4
            },
            6,
            7]
    }]
});


document.getElementById('regular').addEventListener('click', () => {
    chart.tooltip.update({ shared: false, split: false });
});

document.getElementById('shared').addEventListener('click', () => {
    chart.tooltip.update({ shared: true, split: false });
});

document.getElementById('split').addEventListener('click', () => {
    chart.tooltip.update({ shared: false, split: true });
});

css:
@import "https://code.highcharts.com/css/highcharts.css";

#outer {
    max-width: 800px;
    min-width: 320px;
    margin: 0 auto;
}

#container {
    height: 400px;
}

.apples {
    fill: darkgreen;
    stroke: darkgreen;
}

.oranges {
    fill: yellow;
    stroke: yellow;
}

.apples .highcharts-point {
    fill: lightgreen;
    stroke: lightgreen;
}

.oranges .highcharts-point {
    fill: orange;
    stroke: orange;
}

.oranges .blood-orange {
    fill: red;
    stroke: red;
}

.highcharts-tooltip .apples {
    fill: lightgreen;
    stroke: lightgreen;
}

.highcharts-tooltip .oranges {
    fill: orange;
    stroke: orange;
}

.highcharts-tooltip .blood-orange {
    fill: red;
    stroke: red;
}

