css:
@import url("https://code.highcharts.com/css/highcharts.css");

#container {
    min-width: 300px;
    max-width: 800px;
    height: 400px;
    margin: 1em auto;
}

:root {
    --highcharts-color-0: #666;
    --highcharts-color-1: #7cb5ec;
    --highcharts-color-2: #000;
    --highcharts-color-3: #90ed7d;
    --highcharts-color-4: #f7a35c;
}

.highcharts-sankey-series .highcharts-data-label text {
    fill: white;
    text-shadow: 1px 0 0 black, 1px 1px 1px black;
}

npm:
// 安装
npm install highcharts@11.0.1 --save

// import 模块 
import Highcharts from 'highcharts';
import Sankey     from 'highcharts/modules/sankey';
import Exporting  from 'highcharts/modules/exporting';

// 初始化模块
Sankey(Highcharts);
Exporting(Highcharts);
