js:
Highcharts.chart('container', {
    chart: {
        styledMode: true
    },
    title: {
        text: 'Series cursor by CSS'
    },
    xAxis: {
        categories: ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec']
    },
    plotOptions: {
        series: {
            events: {
                click: function () {
                    alert('You just clicked the graph');
                }
            }
        }
    },

    series: [{
        data: [1, 3, 2, 4],
        type: 'column'
    }, {
        data: [5, 4, 6, 7],
        type: 'line',
        dataLabels: {
            enabled: true
        }
    }]
});

css:
@import "https://code.highcharts.com/css/highcharts.css";

#container {
    height: 400px;
    min-width: 300px;
    max-width: 800px;
    margin: 0 auto;
}

.highcharts-series-0 {
    cursor: pointer;
}

.highcharts-series-1 {
    cursor: pointer;
}

