js:
Highcharts.chart('container', {
    chart: {
        type: 'cylinder',
        options3d: {
            enabled: true,
            alpha: 15,
            beta: 15,
            depth: 50,
            viewDistance: 25
        }
    },
    title: {
        text: 'Number of confirmed COVID-19'
    },
    subtitle: {
        text: 'Source: ' +
            '<a href="https://www.fhi.no/en/id/infectious-diseases/coronavirus/daily-reports/daily-reports-COVID19/"' +
            'target="_blank">FHI</a>'
    },
    xAxis: {
        categories: ['0-9', '10-19', '20-29', '30-39', '40-49', '50-59', '60-69', '70-79', '80-89', '90+'],
        title: {
            text: 'Age groups'
        }
    },
    yAxis: {
        title: {
            margin: 20,
            text: 'Reported cases'
        }
    },
    tooltip: {
        headerFormat: '<b>Age: {point.x}</b><br>'
    },
    plotOptions: {
        series: {
            depth: 25,
            colorByPoint: true
        }
    },
    series: [{
        data: [95321, 169339, 121105, 136046, 106800, 58041, 26766, 14291,
            7065, 3283],
        name: 'Cases',
        showInLegend: false
    }]
});

css:
#container {
    height: 400px;
}

.highcharts-figure,
.highcharts-data-table table {
    min-width: 310px;
    max-width: 800px;
    margin: 1em auto;
}

.highcharts-data-table table {
    font-family: Verdana, sans-serif;
    border-collapse: collapse;
    border: 1px solid #ebebeb;
    margin: 10px auto;
    text-align: center;
    width: 100%;
    max-width: 500px;
}

.highcharts-data-table caption {
    padding: 1em 0;
    font-size: 1.2em;
    color: #555;
}

.highcharts-data-table th {
    font-weight: 600;
    padding: 0.5em;
}

.highcharts-data-table td,
.highcharts-data-table th,
.highcharts-data-table caption {
    padding: 0.5em;
}

.highcharts-data-table thead tr,
.highcharts-data-table tr:nth-child(even) {
    background: #f8f8f8;
}

.highcharts-data-table tr:hover {
    background: #f1f7ff;
}

npm:
// 安装
npm install highcharts@11.0.1 --save

// import 模块 
import Highcharts    from 'highcharts';
import Highcharts3d  from 'highcharts/highcharts-3d';
import Cylinder      from 'highcharts/modules/cylinder';
import Exporting     from 'highcharts/modules/exporting';
import ExportData    from 'highcharts/modules/export-data';
import Accessibility from 'highcharts/modules/accessibility';

// 初始化模块
Highcharts3d(Highcharts);
Cylinder(Highcharts);
Exporting(Highcharts);
ExportData(Highcharts);
Accessibility(Highcharts);
