// Create an array with data from y = 1 / x
const functionData = [];
for (let x = -6; x < 6; x += 0.01) {
    // Note: Push y = null for x = 0
    functionData.push([
        x, Math.round(x * 100) ? 1 / x : null
    ]);
}

// Create the chart
const chart = Highcharts.chart('container', {
    chart: {
        marginTop: 40,
        marginLeft: 10,
        marginRight: 10,
        marginBottom: 20
    },
    title: {
        text: 'Sonified mathematical function',
        align: 'left'
    },
    sonification: {
        duration: 8000,
        defaultInstrumentOptions: {
            instrument: 'basic1',
            roundToMusicalNotes: false
        }
    },
    accessibility: {
        landmarkVerbosity: 'one'
    },
    xAxis: {
        min: -6,
        max: 6,
        gridLineWidth: 1,
        tickInterval: 1,
        lineColor: '#343',
        offset: -245
    },
    yAxis: {
        min: -6,
        max: 6,
        tickInterval: 1,
        lineWidth: 1,
        lineColor: '#343',
        offset: -265,
        title: {
            text: null
        }
    },
    legend: {
        enabled: false
    },
    tooltip: {
        headerFormat: '',
        pointFormat: 'y = {point.y:.2f}'
    },
    series: [{
        data: functionData
    }]
});

document.getElementById('sonify').onclick = function () {
    chart.toggleSonify();
};

css:
.highcharts-figure {
    width: 550px;
    margin: 0 auto;
    position: relative;
}

#container {
    height: 550px;
}

#sonify {
    position: absolute;
    top: 0;
    right: 8px;
    z-index: 1;
    background-color: #fff;
    border: 1px solid #25386f;
    color: #25386f;
    font-size: 0.9rem;
    min-height: 25px;
    font-weight: 500;
    border-radius: 4px;
    padding: 0.375rem 0;
    width: 7rem;
    margin-bottom: 0.25rem;
    margin-top: 0.25rem;
    text-align: center;
    cursor: pointer;
}

#sonify:hover {
    background-color: #25386f;
    color: #fff;
}

