js:
Highcharts.chart('container', {
    chart: {
        animation: false
    },

    title: {
        text: 'Highcharts draggable bubbles demo'
    },

    tooltip: {
        valueDecimals: 2
    },

    series: [{
        type: 'bubble',
        cursor: 'move',
        dragDrop: {
            draggableX: true,
            draggableY: true
        },
        data: [
            [100, 240, 3],
            [200, 130, 10],
            [450, 290, 15]
        ]
    }],

    yAxis: {
        softMin: 0,
        softMax: 350
    }
});

npm:
// 安装
npm install highcharts@10.3.2 --save

// import 模块 
import Highcharts      from 'highcharts';
import HighchartsMore  from 'highcharts/highcharts-more';
import DraggablePoints from 'highcharts/modules/draggable-points';
import HighchartsZh_CN from 'https://code.hcharts.cn/plugins/highcharts-zh_CN.js';

// 初始化模块
HighchartsMore(Highcharts);
DraggablePoints(Highcharts);
HighchartsZh_CN(Highcharts);
