js:
Highcharts.setOptions({
    lang: {
        drillUpText: '<< Terug naar {series.name}'
    }
});

// Create the chart
Highcharts.chart('container', {
    chart: {
        type: 'column'
    },
    title: {
        text: 'Drilldown label styling'
    },
    subtitle: {
        text: '<span style="color: red">Note: the <em>drillUpButton</em> option is deprecated. Use <em>drilldown.breadcrumbs</em> instead.'
    },
    xAxis: {
        type: 'category'
    },

    legend: {
        enabled: false
    },

    plotOptions: {
        series: {
            borderWidth: 0,
            dataLabels: {
                enabled: true
            }
        }
    },

    series: [{
        name: 'Things',
        colorByPoint: true,
        data: [{
            name: 'Dieren',
            y: 5,
            drilldown: 'animals'
        }, {
            name: 'Fruit',
            y: 2,
            drilldown: 'fruits'
        }, {
            name: 'Auto\'s',
            y: 4
        }]
    }],
    drilldown: {
        breadcrumbs: {
            showFullPath: false
        },
        drillUpButton: {
            relativeTo: 'spacingBox',
            position: {
                y: 0,
                x: 0
            },
            theme: {
                fill: 'white',
                'stroke-width': 1,
                stroke: 'silver',
                r: 0,
                states: {
                    hover: {
                        fill: '#a4edba'
                    },
                    select: {
                        stroke: '#039',
                        fill: '#a4edba'
                    }
                }
            }

        },
        series: [{
            id: 'animals',
            data: [
                ['Katten', 4],
                ['Honden', 2],
                ['Koeien', 1],
                ['Schapen', 2],
                ['Varkens', 1]
            ]
        }, {
            id: 'fruits',
            data: [
                ['Appels', 4],
                ['Sinaasappels', 2]
            ]
        }]
    }
});

npm:
// 安装
npm install highcharts@10.3.2 --save

// import 模块 
import Highcharts      from 'highcharts';
import Drilldown       from 'highcharts/modules/drilldown';
import HighchartsZh_CN from 'https://code.hcharts.cn/plugins/highcharts-zh_CN.js';

// 初始化模块
Drilldown(Highcharts);
HighchartsZh_CN(Highcharts);
