    title: {
        text: 'Highcharts sourceWidth and sourceHeight demo'
    },

    subtitle: {
        text: 'The on-screen chart is 600x400.<br/>The exported chart is 800x400<br/>(sourceWidth and sourceHeight multiplied by scale)',
