js:
// Highcharts 3.0.10, Issue #2662
// Bubble chart gets truncated when x and y values are close to integral
Highcharts.chart('container', {
    chart: {
        type: 'bubble'
    },
    title: {
        text: 'Highcharts Bubbles'
    },
    series: [{
        data: [[2.3, 3.1, 1]]
    }]
});

css:
#container {
    height: 300px;
    width: 500px;
}

.info {
    font: 0.7em sans-serif;
    padding: 0.5em;
    margin: 0.5em;
    background: #fcffc5;
    border-radius: 0.5em;
    border: 1px solid gray;
}

.info::before {
    content: "i";
    margin: 0.5em;
    font: 1.5em serif bold;
    font-style: italic;
    color: green;
}

npm:
// 安装
npm install highcharts@10.3.2 --save

// import 模块 
import Highcharts      from 'highcharts';
import HighchartsMore  from 'highcharts/highcharts-more';
import HighchartsZh_CN from 'https://code.hcharts.cn/plugins/highcharts-zh_CN.js';

// 初始化模块
HighchartsMore(Highcharts);
HighchartsZh_CN(Highcharts);
