    title: {
        text: 'Experimental vs Predicted Values'
    },
    xAxis: {
        title: {
            text: 'Experimental Measurements'
        },
        gridLineWidth: 1
    },
    yAxis: {
        title: {
            text: 'Predicted Values'
        },
        gridLineWidth: 1
    },
    legend: {
        enabled: false
    },
    series: [{
        name: 'Markers',
        type: 'scatter',
        data: [
            [16, 75],
            [83, 102],
            [83, 31],
            [132, 150],
            [272, 231],
            [328, 290]
        ]
