js:
Highcharts.chart('container', {
    chart: {
        type: 'networkgraph'
    },

    series: [{
        dataLabels: {
            enabled: true,
            borderColor: '#000000',
            borderWidth: 1,
            allowOverlap: true
        },
        marker: {
            radius: 35
        },
        data: [{
            from: 'A',
            to: 'B'
        }]
    }]
});

npm:
// 安装
npm install highcharts@11.0.0 --save

// import 模块 
import Highcharts   from 'highcharts';
import Networkgraph from 'highcharts/modules/networkgraph';

// 初始化模块
Networkgraph(Highcharts);
