js:
Highcharts.chart('container', {

    chart: {
        height: 600,
        inverted: true
    },

    title: {
        text: 'Highcharts Centered Org Chart'
    },

    series: [{
        type: 'organization',
        name: 'Highcharts Org Chart',
        keys: ['from', 'to'],
        data: [
            ['CTO', 'CEO'],
            ['CPO', 'CEO'],
            ['CEO', 'CPO'],
            ['CEO', 'CSO'],
            ['CEO', 'CMO'],
            ['CEO', 'HR']
        ],
        nodes: [{
            id: 'CTO',
            column: 0
        }, {
            id: 'CPO',
            column: 0
        }, {
            id: 'CEO',
            color: '#980104',
            borderRadius: '50%'
        }],
        colorByPoint: false,
        dataLabels: {
            color: 'white'
        },
        nodeWidth: '25%' // = height in the inverted chart
    }],
    exporting: {
        allowHTML: true,
        sourceWidth: 800,
        sourceHeight: 600
    }

});

css:
#container {
    min-width: 300px;
    max-width: 800px;
    margin: 1em auto;
    border: 1px solid silver;
}

#csv {
    display: none;
}

npm:
// 安装
npm install highcharts@11.0.1 --save

// import 模块 
import Highcharts   from 'highcharts';
import Sankey       from 'highcharts/modules/sankey';
import Organization from 'highcharts/modules/organization';
import Exporting    from 'highcharts/modules/exporting';

// 初始化模块
Sankey(Highcharts);
Organization(Highcharts);
Exporting(Highcharts);
