js:
Highcharts.chart('container', {
    title: {
        text: 'Organization Chart: Different type of links'
    },
    series: [{
        marker: {
            radius: 30
        },
        link: {
            type: 'straight'
        },
        type: 'organization',
        keys: ['from', 'to', 'link.type'],
        data: [
            ['A', 'B', 'default'],
            ['B', 'C'],
            ['B', 'E', 'curved'],
            ['A', 'D']
        ]
    }],
    exporting: {
        allowHTML: true,
        sourceWidth: 800,
        sourceHeight: 600
    }
});

npm:
// 安装
npm install highcharts@10.3.2 --save

// import 模块 
import Highcharts    from 'highcharts';
import Sankey        from 'highcharts/modules/sankey';
import Organization  from 'highcharts/modules/organization';
import Exporting     from 'highcharts/modules/exporting';
import Accessibility from 'highcharts/modules/accessibility';
import Zh_cn         from 'https://code.hcharts.cn/plugins/zh_cn.js';

// 初始化模块
Sankey(Highcharts);
Organization(Highcharts);
Exporting(Highcharts);
Accessibility(Highcharts);
Zh_cn(Highcharts);
