js:
Highcharts.chart('container', {

    title: {
        text: 'Highcharts Horizontal Org Chart'
    },

    series: [{
        type: 'organization',
        name: 'Organization',
        keys: ['from', 'to'],
        data: [
            ['Shareholders', 'Board'],
            ['Board', 'CEO'],
            ['CEO', 'CTO'],
            ['CEO', 'CPO'],
            ['CEO', 'CSO'],
            ['CEO', 'CMO'],
            ['CEO', 'HR'],
            ['CTO', 'Product'],
            ['CTO', 'Web'],
            ['CSO', 'Sales'],
            ['CMO', 'Market']
        ],
        levels: [{
            level: 0,
            color: 'silver',
            dataLabels: {
                color: 'black'
            },
            height: 25
        }, {
            level: 1,
            color: 'silver',
            dataLabels: {
                color: 'black'
            },
            height: 25
        }, {
            level: 2,
            color: '#980104'
        }, {
            level: 4,
            color: '#359154'
        }],
        colorByPoint: false,
        nodeWidth: '16%'
    }],
    exporting: {
        allowHTML: true,
        sourceWidth: 800,
        sourceHeight: 600
    }

});

css:
#container {
    min-width: 300px;
    max-width: 800px;
    margin: 1em auto;
    border: 1px solid silver;
}

npm:
// 安装
npm install highcharts@11.0.1 --save

// import 模块 
import Highcharts   from 'highcharts';
import Sankey       from 'highcharts/modules/sankey';
import Organization from 'highcharts/modules/organization';
import Exporting    from 'highcharts/modules/exporting';

// 初始化模块
Sankey(Highcharts);
Organization(Highcharts);
Exporting(Highcharts);
