        type: 'venn',
        name: 'The Unattainable Triangle',
        data: [{
            sets: ['Good'],
            value: 2,
            name: 'Good'
        }, {
            sets: ['Fast'],
            value: 2,
            name: 'Fast'
        }, {
            sets: ['Cheap'],
            value: 2,
            name: 'Cheap'
