js:
// Create some data
var data = [];
for (var i = 0; i < 1000; ++i) {
    data.push(Math.sin(i / 50) * 3 + Math.random() - 0.5);
}

// Show the chart
var chart = Highcharts.chart('container', {
    title: {
        text: 'Large dataset sonified',
        align: 'left',
        margin: 35
    },
    legend: {
        enabled: false
    },
    accessibility: {
        landmarkVerbosity: 'one'
    },
    lang: {
        accessibility: {
            chartContainerLabel: 'Big data sonification. Highcharts interactive chart.'
        }
    },
    sonification: {
        events: {
            onPlay: function () {
                document.getElementById('btn').textContent = 'Stop';
            },
            onStop: function () {
                document.getElementById('btn').textContent = 'Play';
            }
        },
        defaultInstrumentOptions: {
            instrument: 'square',
            mapping: {
                lowpass: {
                    frequency: 2000
                },
                highpass: {
                    frequency: 200
                }
            }
        }
    },
    tooltip: {
        valueDecimals: 2
    },
    series: [{
        data: data
    }]
});

// Play/Stop button
document.getElementById('btn').onclick = function () {
    var btn = this;
    chart.toggleSonify(true, function () {
        btn.textContent = 'Play';
    });
};

css:
#btn {
    position: absolute;
    top: 5px;
    right: 5px;
    z-index: 10;
    background-color: #fff;
    border: 1px solid #25386f;
    color: #25386f;
    font-size: 0.9rem;
    min-height: 30px;
    font-weight: 500;
    border-radius: 4px;
    padding: 0.375rem 0;
    width: 7rem;
    margin-bottom: 0.25rem;
    margin-top: 0.25rem;
    text-align: center;
    cursor: pointer;
}

#btn:hover {
    background-color: #25386f;
    color: #fff;
}

.highcharts-figure {
    min-width: 360px;
    max-width: 850px;
    margin: 0 auto;
    position: relative;
}

