var chart = Highcharts.chart('container', {
    title: {
        text: 'Timeline',
        align: 'left',
        margin: 25
    },
    legend: {
        enabled: false
    },
    tooltip: {
        shared: true
    },
    sonification: {
        duration: 3000,
        order: 'simultaneous'
    },
    series: [{
        data: [4, 5, 6, 5, 7, 9, 11, 13]
    }, {
        data: [1, 3, 4, 2]
    }]
});

document.getElementById('sonify').onclick = function () {
    chart.toggleSonify(false, () => {
        // Play a note on end without setting an event handler in options
        chart.sonification.playNote('vibraphone', {
            note: 'g5', noteDuration: 400, volume: 0.7
        });
    });
};

css:
.highcharts-figure {
    max-width: 700px;
    margin: 0 auto;
    position: relative;
}

#sonify {
    position: absolute;
    top: 5px;
    right: 5px;
    z-index: 10;
    background-color: #fff;
    border: 1px solid #25386f;
    color: #25386f;
    font-size: 0.9rem;
    min-height: 30px;
    font-weight: 500;
    border-radius: 4px;
    padding: 0.375rem 0;
    width: 10rem;
    margin-bottom: 0.25rem;
    margin-top: 0.25rem;
    text-align: center;
    cursor: pointer;
}

#sonify:hover {
    background-color: #25386f;
    color: #fff;
}

