js:
var chart = Highcharts.chart('container', {
    title: {
        text: 'Time mapping',
        align: 'left',
        margin: 25
    },
    subtitle: {
        text: 'Points are played from bottom of y-axis and up',
        align: 'left'
    },
    chart: {
        type: 'scatter'
    },
    sonification: {
        duration: 4000,
        defaultInstrumentOptions: {
            mapping: {
                time: 'y' // Time is mapped to Y values
            }
        }
    },
    legend: {
        enabled: false
    },
    tooltip: {
        headerFormat: '',
        pointFormat: '{point.x},{point.y}'
    },
    series: [{
        data: [
            [16, -5],
            [7, -4],
            [19, -3],
            [3, -2],
            [21, -1],
            [-2, 0],
            [7, 1],
            [-3, 2],
            [16, 3],
            [17, 4],
            [11, 5],
            [-1, 6]
        ],
        color: 'rgba(30, 30, 200, 0.7)',
        marker: {
            radius: 5
        }
    }]
});

document.getElementById('sonify').onclick = function () {
    chart.toggleSonify();
};

css:
.highcharts-figure {
    max-width: 600px;
    margin: 0 auto;
    position: relative;
}

#sonify {
    position: absolute;
    top: 15px;
    right: 5px;
    z-index: 10;
    background-color: #fff;
    border: 1px solid #25386f;
    color: #25386f;
    font-size: 0.9rem;
    min-height: 30px;
    font-weight: 500;
    border-radius: 4px;
    padding: 0.375rem 0;
    width: 9rem;
    margin-bottom: 0.25rem;
    margin-top: 0.25rem;
    text-align: center;
    cursor: pointer;
}

#sonify:hover {
    background-color: #25386f;
    color: #fff;
}

npm:
// 安装
npm install highcharts@11.0.1 --save

// import 模块 
import Highcharts    from 'highcharts';
import Sonification  from 'highcharts/modules/sonification';
import Accessibility from 'highcharts/modules/accessibility';

// 初始化模块
Sonification(Highcharts);
Accessibility(Highcharts);
