js:
var chart = Highcharts.chart('container', {
    title: {
        text: 'Inverted pitch polarity',
        align: 'left',
        margin: 25
    },
    legend: {
        enabled: false
    },
    sonification: {
        duration: 4000,
        defaultInstrumentOptions: {
            mapping: {
                pitch: {
                    mapTo: '-y',
                    min: 'c2',
                    max: 'c7'
                }
            }
        }
    },
    series: [{
        data: [4, 5, 6, 5, 7, 9, 11, 13]
    }, {
        data: [1, 3, 4, 2]
    }]
});

document.getElementById('sonify').onclick = function () {
    chart.toggleSonify();
};

css:
.highcharts-figure {
    max-width: 700px;
    margin: 0 auto;
    position: relative;
}

#sonify {
    position: absolute;
    top: 5px;
    right: 5px;
    z-index: 10;
    background-color: #fff;
    border: 1px solid #25386f;
    color: #25386f;
    font-size: 0.9rem;
    min-height: 30px;
    font-weight: 500;
    border-radius: 4px;
    padding: 0.375rem 0;
    width: 10rem;
    margin-bottom: 0.25rem;
    margin-top: 0.25rem;
    text-align: center;
    cursor: pointer;
}

#sonify:hover {
    background-color: #25386f;
    color: #fff;
}

npm:
// 安装
npm install highcharts@11.0.0 --save

// import 模块 
import Highcharts    from 'highcharts';
import Sonification  from 'highcharts/modules/sonification';
import Accessibility from 'highcharts/modules/accessibility';

// 初始化模块
Sonification(Highcharts);
Accessibility(Highcharts);
