js:
Highcharts.setOptions({
    lang: {
        accessibility: {
            chartContainerLabel: 'Earcons on series. Highcharts interactive chart.'
        }
    }
});

var chart = Highcharts.chart('container', {
    chart: {
        type: 'areaspline'
    },
    title: {
        text: 'Click series to sonify'
    },
    subtitle: {
        text: 'Crossing triggers earcon'
    },
    legend: {
        enabled: false
    },
    plotOptions: {
        series: {
            sonification: {
                duration: 2000,
                pointPlayTime: 'x',
                instruments: [{
                    instrument: 'sineMajor',
                    minFrequency: 220,
                    maxFrequency: 2200,
                    instrumentMapping: {
                        volume: 0.8,
                        duration: 250,
                        pan: 'x',
                        frequency: 'y'
                    }
                }],
                earcons: [{
                    // Define the earcon we want to play
                    earcon: new Highcharts.sonification.Earcon({
                        // Global volume for earcon
                        volume: 0.3,
                        // Play two instruments for this earcon
                        instruments: [{
                            instrument: 'triangleMajor',
                            playOptions: {
                                // Play a rising frequency
                                frequency: function (time) {
                                    return time * 1760;
                                },
                                duration: 200,
                                pan: 0.8 // Pan 80% right
                            }
                        }, {
                            instrument: 'triangleMajor',
                            playOptions: {
                                // Play another rising frequency
                                frequency: function (time) {
                                    return time * 2217;
                                },
                                duration: 200,
                                pan: -0.8 // Pan 80% left
                            }
                        }]
                    }),
                    // Play this earcon if this point is crossing
                    // another series.
                    condition: function (point) {
                        var chart = point.series.chart;
                        // Go through the other series in the chart
                        // and see if there is a point with the same
                        // value. If so we return true, and the earcon
                        // is played.
                        return chart.series.some(function (series) {
                            return series !== point.series &&
                                series.points.some(function (p) {
                                    return p.y === point.y &&
                                        p.x === point.x;
                                });
                        });
                    }
                }],
                events: {
                    onPointStart: function (e, point) {
                        point.onMouseOver();
                        document.getElementById('stop').style.visibility = 'visible';
                        document.getElementById('stop').focus();
                    },
                    onSeriesEnd: function () {
                        document.getElementById('stop').style.visibility = 'hidden';
                    }
                }
            },
            events: {
                click: function () {
                    // Sonify the series when clicked
                    this.sonify();
                }
            }
        }
    },
    series: [{
        data: [1, 2, 4, 5, 7, 9, 11, 13]
    }, {
        data: [4, 5, 9, 5, 2, 1]
    }]
});

document.getElementById('stop').onclick = function () {
    chart.cancelSonify();
    document.getElementById('stop').style.visibility = 'hidden';
};

css:
.btn.btn-stop {
    background-color: #fff;
    border: 1px solid #25386f;
    color: #25386f;
    width: 20%;
    font-size: 0.9rem;
    min-height: 30px;
    font-weight: 500;
}

.btn {
    background-color: #25386f;
    border: 1 px solid #25386f;
    color: #fff;
    min-height: 40px;
    width: 30%;
    border-radius: 4px;
    font-size: 1.1rem;
    font-weight: 700;
    text-align: center;
    padding: 0.375rem 0.75rem;
    margin-bottom: 0.25rem;
    margin-top: 0.25rem;
}

.text-center {
    text-align: center;
}

.highcharts-figure {
    width: 100%;
    height: 100%;
    margin: 0;
}

#outer {
    min-width: 360px;
    max-width: 800px;
    margin: 0 auto;
    position: relative;
}

#stop {
    visibility: hidden;
}

@media (max-width: 575px) {
    .btn.btn-stop {
        width: 30%;
    }
}

