@import "https://code.highcharts.com/css/highcharts.css";

body {
    margin: 0;
}

.chart-container .thick {
    stroke-width: 1;
    stroke: white;
    color: white;
    transition: all 2s;
}

.chart-container .thin {
    stroke-width: 1;
    stroke: white;
    color: white;
    transition: all 2s;
}

.chart-container .transparent {
    opacity: 100%;
    stroke-width: 1;
    stroke: white;
    color: white;
    transition: all 2s;
}

.chart-container .highcharts-area,
.chart-container .highcharts-arearange {
    fill-opacity: 1;
    stroke: transparent;
}

.line {
    color: #fff;
    stroke: #fff;
    stroke-width: 1px;
}

.chart-container .highcharts-container {
    position: relative;
    overflow: hidden;
    text-align: left;
    line-height: normal;
    z-index: 0;
    -webkit-tap-highlight-color: rgb(0 0 0 / 0%);
    font-family: "IBM Plex Sans", sans-serif;
    font-size: 12px;
    user-select: none;
    touch-action: manipulation;
    outline: none;
    top: 0 !important;
}

.chart-container .highcharts-background {
    transition: all 1s;
}

.chart-container .highcharts-grid-line {
    fill: none;
    stroke: transparent;
    z-index: 40;
}

.chart-container .highcharts-xaxis-grid .highcharts-grid-line {
    stroke-width: 1px;
}

.chart-container .highcharts-legend-series-active g.highcharts-series:not(.highcharts-series-hover),
.chart-container .highcharts-legend-point-active .highcharts-point:not(.highcharts-point-hover),
.chart-container .highcharts-legend-series-active .highcharts-markers:not(.highcharts-series-hover),
.chart-container .highcharts-legend-series-active .highcharts-data-labels:not(.highcharts-series-hover) {
    opacity: 100%;
}

.chart-container .highcharts-series-inactive {
    opacity: 100%;
    transition: opacity 50ms;
}

.chart-container .highcharts-tooltip-0 {
    filter: drop-shadow(5px 7px 13px rgb(0 0 0 / 14%)) !important;
    box-shadow: 5px 7px 13px 1px rgb(177 149 149 / 14%);
}

.chart-container .highcharts-tooltip-box {
    stroke-width: 0;
    border: none;
    stroke: transparent;
    fill: transparent;
    fill-opacity: 1;
}

.chart-container .highcharts-tooltip text {
    fill: #fff;
    color: #fff;
}

.chart-container .highcharts-tooltip {
    z-index: 100;
}

.chart-container .highcharts-graph {
    fill: unset;
    stroke-width: unset;
    stroke: unset;
    stroke-linecap: butt;
    stroke-linejoin: round;
}

.chart-container .highcharts-color-3 {
    fill: transparent;
    stroke: transparent;
}

#charts {
    width: 250px;
    height: 250px;
    border-radius: 4px;
    background-color: #2f2b38;
    border: 1px solid #46465c;
}

#charts .highcharts-plot-background,
#charts .highcharts-background {
    fill: #201836;
    stroke: transparent;
}

#charts .berg-tip {
    width: 150px;
    color: #fff;
    white-space: normal;
    font-size: 0.9em;
    font-weight: 500;
}

#charts .berg-tip span {
    font-weight: 700;
}

#charts .wall {
    fill: #46445d;
    stroke: transparent;
}

#charts .cover {
    fill: #201836;
    stroke: transparent;
    transition: fill 2s;
}

#charts .green-line .highcharts-graph {
    fill: transparent;
    stroke-width: 1px;
    stroke: #8bf2b6;
}

#charts .white-line .highcharts-graph {
    fill: transparent;
    stroke-width: 1px;
    stroke: rgb(255 255 255 / 50%);
}

#charts .particles {
