css:
@media print {
    .no-print,
    .no-print * {
        display: none !important;
    }
}

@media print {
    .no-print,
    .no-print * {
        display: none !important;
    }
}

.highed-box-size {
    box-sizing: border-box !important;
    -moz-box-sizing: border-box !important;
    -webkit-box-sizing: border-box !important;
}

.highed-transition {
    transition: 0.2s ease all;
    -moz-transition: 0.2s ease all;
    -webkit-transition: 0.2s ease all;
}

.highed-transition-opacity {
    transition: 0.2s ease opacity;
    -moz-transition: 0.2s ease opacity;
    -webkit-transition: 0.2s ease opacity;
}

.highed-nosel {
    -webkit-touch-callout: none;
    -webkit-user-select: none;
    -khtml-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
    cursor: default !important;
}

.highed-fill {
    width: 100%;
    height: 100%;
}

.highed-vsplitter a {
    color: #afafaf;
}

.highed-vsplitter a:hover {
    color: rgb(222 222 222 / 37%);
}

.highed-vsplitter a:active {
    color: #242424;
}

.highed-scrollbar::-webkit-scrollbar-track {
    background-color: #fff;
}

.highed-scrollbar::-webkit-scrollbar-thumb {
    background-color: #afafaf;
}

.highed-scrollbar::-webkit-scrollbar {
    width: 12px;
    background-color: #fff;
}

.highed-attention {
    position: absolute;
    background-color: rgb(27 112 240 / 67%);
    transition: 0.3s ease all;
}

.highed-header {
    background: #fff !important;
    color: #25386f !important;
}

.highed-logo {
    background-repeat: no-repeat;
    background-size: 90% auto;
    height: 40px;
    width: 180px;
    background-position: left -5px;
    box-sizing: border-box !important;
    -moz-box-sizing: border-box !important;
    -webkit-box-sizing: border-box !important;
}

.highed-inline-blocks {
    /* display: inline-block; */
    float: left;
}

.highed-credits {
    font-size: 10px;
    padding: 4px;
    font-weight: 100;
    font-family: "IBM Plex Sans", sans-serif !important;
}

.highed-done-button {
    padding: 5px 7px;
    border: none;
    background: #fff;
    color: #4c4c4c;
    font-size: 12px;
    font-weight: bold;
    border-bottom-right-radius: 5px;
    border-bottom-left-radius: 5px;
    margin-right: 5px;
    height: 100%;
    cursor: pointer;
    outline: none;
    transition: 0.2s ease all;
    -moz-transition: 0.2s ease all;
    -webkit-transition: 0.2s ease all;
    box-sizing: border-box !important;
    -moz-box-sizing: border-box !important;
    -webkit-box-sizing: border-box !important;
}

.highed-done-button:hover {
    background: #fff;
}

.highed-stretch {
    width: 100%;
}

.highed-ok-button {
    border: none;
    outline: none;
    margin-bottom: 10px;
    margin-top: 10px;
    text-align: center;
    background: #25386f;
    color: #fafafa;
    border-radius: 3px;
    padding: 7px 10px;
    font-size: 12px;
    font-weight: bold;
    text-transform: uppercase;
    letter-spacing: 1px;
    transition: 0.2s ease all;
    -moz-transition: 0.2s ease all;
    -webkit-transition: 0.2s ease all;
    -webkit-touch-callout: none;
    -webkit-user-select: none;
    -khtml-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
    cursor: default !important;
}

.grey.negative {
    background-color: white !important;
    border: 1px solid #bababa !important;
    color: #bababa !important;
}

.highed-ok-button.mini {
    min-width: 50px;
    width: 70px;
    min-height: 20px;
    height: 22px;
    margin-top: 9px;
    padding-top: 5px;
    font-size: 11px;
}

.highed-import-button {
    background-color: white;
    border: 1px solid #25386f;
    color: #25386f;
    text-transform: initial;
    border-radius: 59px;
    margin-bottom: 0;
    padding: 7px 11px;
    min-width: 101px;
    font-size: 9px;
    margin-top: 0;
    margin-right: 5px;
    vertical-align: top;
    cursor: pointer !important;
}

.highed-import-button.green {
    background-color: #25386f;
    border: 0;
    color: white;
    font-weight: 800;
    font-size: 11px;
    font-family: "IBM Plex Sans", sans-serif;
}

.highed-import-button.padded {
    padding: 9px 18px;
}

.highed-import-button.small {
    min-width: 53px;
}

.highed-import-button.grey {
    border: 1px solid #a3a3a3;
    background-color: #bababa;
    color: white;
    font-size: 11px;
}

.highed-import-button.negative {
    border: 1px solid #25386f;
    background-color: #25386f;
    color: white;
    font-size: 11px;
}

.highed-import-button.grey:hover {
    border: 1px solid #a3a3a3;
    background-color: #bababa;
    color: white;
    font-size: 11px;
}

.highed-page-title {
    margin: 25px 0 16px 30px;
}

.highed-page-title h3 {
    color: #25386f;
    font-size: 24px;
    margin-left: 5px;
    display: inline;
}

.highed-page-title .highed-toolbox-help {
    display: inline;
    position: relative;
    top: -9px;
    left: -7px;
    font-size: 10px;
    margin-right: 0;
}

.highed-done-pane {
    padding: 0 20px;
    position: relative;
}

.highed-step-body.highed-done-pane .highed-ok-button {
    font-size: 15px;
    padding: 10px;
}

.highed-step-body.highed-done-pane .highed-ok-button::before {
    content: "\f00c";
    font-family: fontAwesome, sans-serif;
    font-size: 17px;
    margin-right: 5px;
}

.highed-ok-button:hover {
    background: rgb(27 112 240 / 67%);
    color: white;
}

.highed-import-button:hover {
    background: #25386f;
    color: white;
}

.highed-background-normal {
    background: #eaf1fd;
    color: #242424;
}

.highed-titlebar {
    text-align: center;
    font-weight: 200;
}

.CodeMirror {
    font-family: "Open Sans", sans-serif;
    font-size: 11px;
}

@media screen and (max-width: 768px) {
    .highed-page-title {
        margin: 25px 0 16px 5px;
    }
}

.highed-container {
    font-family: "IBM Plex Sans", sans-serif !important;
    color: #242424;
    background: #eaf1fd;
    border: 1px solid #e8e8e8;
    height: 100%;
    width: 100%;
    box-sizing: border-box !important;
    -moz-box-sizing: border-box !important;
    -webkit-box-sizing: border-box !important;
}

.highed-chart-container {
    position: relative;
    margin: 0;
}

.highed-chart-container-icon {
    float: left;
    width: 40px;
    height: 40px;
    background-repeat: no-repeat;
    background-position: center;
    background-color: #fff;
    padding: 4px;
    box-sizing: border-box !important;
    -moz-box-sizing: border-box !important;
    -webkit-box-sizing: border-box !important;
}

@media (max-width: 700px) {
    .highed-hsplitter .panel.highed-chart-preview-bar {
        display: none !important;
    }
}

@media (min-width: 700px) {
    .highed-hsplitter .panel.highed-chart-preview-bar {
        background: #e0e0e0;
        border-left: 1px solid #444;
        padding: 10px;
    }
}

.highed-container .titlebar {
    color: #242424;
    background: #eaf1fd;
    height: 48px;
    width: 100%;
    font-weight: 100;
    position: relative;
    padding: 5px;
    line-height: 40px;
    box-sizing: border-box !important;
    -moz-box-sizing: border-box !important;
    -webkit-box-sizing: border-box !important;
    -webkit-touch-callout: none;
    -webkit-user-select: none;
    -khtml-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
    cursor: default !important;
}

.highed-container .icons {
    position: absolute;
    right: 10px;
    top: 5px;
    height: 100%;
}

.highed-expand-container {
    position: absolute;
    z-index: 2;
    width: 0;
    height: 100%;
    top: 0;
    right: 0;
    display: none;
    background-color: #fff;
}

.highed-warningbar {
    background: #e6a031 !important;
}

.highed-warningbar .highed-errorbar-body {
    background: #f5ab35;
}

.highed-errorbar {
    opacity: 0%;
    pointer-events: none;
    width: 40%;
    margin-left: auto;
    margin-right: auto;
    background: #a55;
    color: #fafafa;
    position: fixed;
    z-index: 100000;
    bottom: 0;
    left: 22px;
    margin-bottom: 15px;
    border-radius: 3px;
    border: 1px solid rgb(128 128 128 / 43.9%);
    box-shadow: 2px 3px 6px 0 rgb(0 0 0 / 25.9%);
}

.highed-errorbar-headline {
    font-size: 15px;
    text-align: left;
    padding: 5px;
}

.highed-errorbar-headline-text {
    width: calc(85%);
    display: inline-block;
}

.highed-errorbar-close {
    width: 10px;
    padding-right: 5px;
    float: right;
    vertical-align: top;
    cursor: pointer;
}

.highed-errorbar-body {
    overflow-y: auto;
    min-height: 23px;
    padding: 5px;
    background: #b36666;
    padding-top: 10px;
    font-size: 12px;
}

.highed-errorbar a {
    color: #fafafa !important;
}

.highed-errorbar-more {
    margin-top: 10px;
}

.highed-icon {
    width: 20px;
    height: 20px;
    margin-left: 10px;
    margin-right: 10px;
    text-align: center;
    transition: 0.2s ease all;
    -moz-transition: 0.2s ease all;
    -webkit-transition: 0.2s ease all;
}

.highed-icon:hover {
    color: #42c8c0;
}

.highed-dimmer {
    opacity: 0%;
    pointer-events: none;
    width: 100%;
    height: 100%;
    position: fixed;
    left: 0;
    top: 0;
    z-index: 999;
    background: #000;
    transition: 0.2s ease all;
    -moz-transition: 0.2s ease all;
    -webkit-transition: 0.2s ease all;
}

.highed-overlay-modal {
    width: 0;
    height: 0;
    overflow: hidden;
    border-radius: 4px;
    font-family: "IBM Plex Sans", sans-serif !important;
    color: #afafaf;
    background: #fff;
    position: fixed;
    z-index: 10000;
    top: 50%;
    left: -20000px;
    transform: translate(-50%, -50%);
    opacity: 0%;
    border: 1px solid #666;
    pointer-events: none;
    transition: 0.2s ease all;
    -moz-transition: 0.2s ease all;
    -webkit-transition: 0.2s ease all;
}

.highed-overlaymodal-close {
    position: absolute;
    right: 12px;
    top: 10px;
    color: black;
    cursor: pointer;
}

.highed-modal-container .highed-modal-buttons-container {
    margin-right: 10px;
    margin-top: 45px;
}

.highed-modal-container textarea {
    width: 98%;
    min-height: 274px;
    border-radius: 4px;
    resize: none;
}

.highed-modal-container div {
    margin-left: 10px;
}

.highed-modal-container .highed-modal-text {
    margin-top: 7px;
    margin-bottom: 7px;
    color: black;
}

.highed-modal-container .highed-modal-label {
    text-align: right;
    padding-right: 5px;
    padding-left: 5px;
}

.highed-modal-container .highed-table-container {
    margin-top: 30px;
    color: black;
    font-size: 11px;
    font-weight: 600;
    margin-right: 30px;
    margin-left: 0;
}

.highed-modal-container .highed-table-container table {
    width: 100%;
}

.highed-modal-container .highed-table-container table .highed-live-data .highed-modal-label {
    max-width: 67px;
}

.highed-modal-container .highed-table-container .highed-imp-input-stretch {
    height: 33px;
}

.highed-modal-container .highed-button-container {
    text-align: center;
    margin-top: 30px;
}

.highed-hsplitter {
    height: 100%;
    width: 100%;
    box-sizing: border-box !important;
    -moz-box-sizing: border-box !important;
    -webkit-box-sizing: border-box !important;
    overflow: hidden;
}

.highed-hsplitter .panel {
    float: left;
    overflow-x: hidden;
    overflow-y: auto;
    height: 100%;
    background: #fff;
    box-sizing: border-box !important;
    -moz-box-sizing: border-box !important;
    -webkit-box-sizing: border-box !important;
}

.highed-hsplitter .left {
    width: 30%;
}

.highed-hsplitter .right {
    width: 70%;
}

.highed-hsplitter-body {
    color: #afafaf;
    width: 100%;
    height: 100%;
    position: relative;
    box-sizing: border-box !important;
    -moz-box-sizing: border-box !important;
    -webkit-box-sizing: border-box !important;
}

.highed-step-body .highed-hsplitter .panel.right .highed-hsplitter-body {
    color: #fafafa;
    padding-left: 10px;
    padding-right: 10px;
    padding-bottom: 10px;
    height: auto;
}

.highed-hsplitter-resize-bar {
    position: absolute;
    width: 5px;
    height: 100%;
    cursor: ew-resize;
    border-left: 1px solid #e8e8e8;
    border-right: 1px solid #e8e8e8;
    box-sizing: border-box !important;
    -moz-box-sizing: border-box !important;
    -webkit-box-sizing: border-box !important;
}

@media (max-width: 550px) {
    .highed-hsplitter-body-responsive {
        float: none !important;
        max-height: 42px;
        width: 100% !important;
    }
}

.highed-vsplitter {
    height: 67vh;
    width: 100%;
    box-sizing: border-box !important;
    -moz-box-sizing: border-box !important;
    -webkit-box-sizing: border-box !important;
}

.highed-vsplitter .panel {
    overflow-y: auto;
    width: 100%;
    background: #fff;
    border: none !important;
    border-radius: 0 !important;
    box-sizing: border-box !important;
    -moz-box-sizing: border-box !important;
    -webkit-box-sizing: border-box !important;
}

.highed-vsplitter .top {
    height: 30%;
    position: relative;
}

.highed-vsplitter .bottom {
    height: 70%;
    position: relative;
}

.highed-vsplitter-body {
    background: #eaf1fd;
    color: #242424;
    width: 100%;
    height: 100%;
    box-sizing: border-box !important;
    -moz-box-sizing: border-box !important;
    -webkit-box-sizing: border-box !important;
}

.highed-tab-control {
    width: 100% !important;
    box-sizing: border-box !important;
    -moz-box-sizing: border-box !important;
    -webkit-box-sizing: border-box !important;
}

.highed-tab-control .tabs {
    background: #25386f;
    color: #fff;
    height: 48px;
    position: relative;
    font-weight: 300;
    font-size: 14px;
    line-height: 40px;
    padding: 5px;
    margin-bottom: 10px;
    overflow: hidden;
    box-sizing: border-box !important;
    -moz-box-sizing: border-box !important;
    -webkit-box-sizing: border-box !important;
    -webkit-touch-callout: none;
    -webkit-user-select: none;
    -khtml-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
    cursor: default !important;
}

.highed-tab-control-more {
    position: absolute;
    right: 0;
    top: 0;
    background: #e3e3e3;
    color: #afafaf;
    width: 30px;
    padding: 5px;
    height: 100%;
    text-align: center;
    line-height: 40px !important;
    display: none;
    box-sizing: border-box !important;
    -moz-box-sizing: border-box !important;
    -webkit-box-sizing: border-box !important;
    transition: 0.2s ease all;
    -moz-transition: 0.2s ease all;
    -webkit-transition: 0.2s ease all;
}

.highed-tab-control-more:hover {
    background: #88a;
}

.highed-tab-control .tab {
    float: left;
    font-weight: 100;
    padding-left: 24px;
    padding-right: 24px;
    cursor: pointer;
    text-align: center;
}

.highed-tab-control .tab-body {
    opacity: 0%;
    width: 100%;
    height: 100%;
    position: absolute;
    left: 0;
    top: 0;
    overflow-y: auto;
    display: none;
    box-sizing: border-box !important;
    -moz-box-sizing: border-box !important;
    -webkit-box-sizing: border-box !important;
    transition: 0.2s ease all;
    -moz-transition: 0.2s ease all;
    -webkit-transition: 0.2s ease all;
}

.highed-tab-control .tab-body-padded {
    padding: 10px;
}

.highed-tab-control .tab-selected {
    font-weight: 600;
}

.highed-tab-control .body {
    position: relative;
    box-sizing: border-box !important;
    -moz-box-sizing: border-box !important;
    -webkit-box-sizing: border-box !important;
}

.highed-tab-control .indicator {
    position: absolute;
    bottom: 0;
    left: 0;
    width: 100px;
    height: 5px;
    background: rgb(222 222 222 / 37%);
    transition: 0.2s ease all;
    -moz-transition: 0.2s ease all;
    -webkit-transition: 0.2s ease all;
}

.highed-list {
    box-sizing: border-box !important;
    -moz-box-sizing: border-box !important;
    -webkit-box-sizing: border-box !important;
    -webkit-touch-callout: none;
    -webkit-user-select: none;
    -khtml-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
    cursor: default !important;
    overflow: hidden;
}

.highed-list .item {
    text-align: left !important;
    color: #242424 !important;
    font-size: 13px !important;
    cursor: pointer;
    display: block;
    background-color: #fff;
    border-top: 1px solid rgb(128 128 128 / 7.84%);
    background-image: none !important;
    padding: 15px 10px;
    box-sizing: border-box !important;
    -moz-box-sizing: border-box !important;
    -webkit-box-sizing: border-box !important;
    transition: 0.2s ease all;
    -moz-transition: 0.2s ease all;
    -webkit-transition: 0.2s ease all;
}

.highed-list .item:hover {
    font-weight: bold;
    text-decoration: none;
}

.highed-list .item:first-child {
    border-top: none !important;
}

.highed-list .item-selected,
.highed-list .item-selected:hover {
    background-color: white;
    color: #25386f !important;
    font-weight: bold;
    cursor: default;
}

.highed-list-compact {
    position: relative;
    display: none;
    text-align: left;
    padding-top: 5px;
    padding-bottom: 5px;
    font-weight: 600;
    font-family: "IBM Plex Sans", sans-serif !important;
    font-size: 20px;
    background: #e3e3e3;
    color: #afafaf;
    box-sizing: border-box !important;
    -moz-box-sizing: border-box !important;
    -webkit-box-sizing: border-box !important;
}

.highed-list-compact .icon {
    margin-right: 30px;
    margin-left: 30px;
}

.item-arrow {
    float: right;
    margin-right: 6px;
}

.annotations-container {
    float: right;
}

.annotations-btn {
    display: inline-block;
    margin-left: 5px;
    border: 1px solid rgb(128 128 128 / 34.9%);
    padding: 5px 7px;
    position: relative;
    border-radius: 4px;
    top: -6px;
    cursor: pointer;
}

.annotations-btn.active {
    background-color: rgb(0 0 255 / 22%);
}

.no-clickable {
    cursor: default !important;
}

.highed-list-suboptions {
    border: 1px solid rgb(140 140 140 / 29%);
    border-radius: 4px;
    margin-left: 5px;
    margin-right: 5px;
}

.highed-list-normal {
    padding-top: 10px;
    padding-left: 5px;
    padding-right: 5px;
}

.highed-list-general-drop-down {
    max-height: 28px;
    transition: max-height 0.15s ease-out;
    overflow: hidden;
}

.highed-list-general-drop-down .highed-list-general-drop-down-header {
    background-color: #eff2fb;
    color: #364f94;
    cursor: pointer;
    padding-left: 5px !important;
    font-weight: bold;
    font-size: 13px;
}

.highed-list-general-drop-down .highed-customizer-table {
    padding-top: 10px;
    padding-left: 5px;
}

.highed-list-general-drop-down.active {
    max-height: 500px;
    transition: max-height 0.25s ease-in;
}

.highed-list-general-drop-down:first-of-type {
    border-radius: 4px;
}

.highed-chart-templates {
    padding: 10px;
}

.highed-chart-templates .highed-dropdown-body {
    font-size: 10px;
    line-height: 15px;
}

.highed-step-body .highed-chart-templates .highed-hsplitter .panel.right .highed-hsplitter-body {
    padding-top: 10px;
}

.highed-chart-template-frame {
    padding-left: 5px;
    padding-right: 5px;
}

.highed-chart-template-title {
    font-size: 10px;
    color: black;
    padding-left: 5px;
}

.highed-chart-template-cat-desc {
    font-size: 0.9em;
    margin-bottom: 10px;
    box-sizing: border-box !important;
    -moz-box-sizing: border-box !important;
    -webkit-box-sizing: border-box !important;
}

.highed-chart-template-description {
    font-size: 0.9em;
    padding-bottom: 5px;
    padding-top: 5px;
}

.highed-chart-template-container {
    vertical-align: top;
    display: inline-block;
    min-width: 100px;
    min-height: 86px;
    margin: 1px;
    border: 1px solid rgb(128 128 128 / 23.1%);
    cursor: pointer;
    box-sizing: border-box !important;
    -moz-box-sizing: border-box !important;
    -webkit-box-sizing: border-box !important;
}

.highed-chart-template-thumbnail {
