@import "https://code.highcharts.com/css/highcharts.css";

body {
    margin: 0;
}

.chart-container .highcharts-container {
    position: relative;
    overflow: hidden;
    text-align: left;
    line-height: normal;
    fill: #fff;
    z-index: 0;
    -webkit-tap-highlight-color: rgb(0 0 0 / 0%);
    font-family: "IBM Plex Sans", sans-serif;
    font-size: 12px;
    user-select: none;
    touch-action: manipulation;
    outline: none;
}

.chart-container .highcharts-grid-line {
    fill: none;
    stroke: transparent;
    z-index: 40;
}

.chart-container .highcharts-xaxis-grid .highcharts-grid-line {
    stroke-width: 1px;
}

.chart-container .highcharts-plot-band,
.highcharts-pane {
    fill: #30426b;
    fill-opacity: 1;
    z-index: 30;
}

.chart-container .highcharts-legend-series-active g.highcharts-series:not(.highcharts-series-hover),
.chart-container .highcharts-legend-point-active .highcharts-point:not(.highcharts-point-hover),
.chart-container .highcharts-legend-series-active .highcharts-markers:not(.highcharts-series-hover),
.chart-container .highcharts-legend-series-active .highcharts-data-labels:not(.highcharts-series-hover) {
    opacity: 100%;
}

.chart-container .highcharts-series-inactive {
    opacity: 100%;
    transition: opacity 50ms;
}

.chart-container .highcharts-tooltip-0 {
    filter: drop-shadow(5px 7px 13px rgb(0 0 0 / 14%)) !important;
    box-shadow: 5px 7px 13px 1px rgb(0 0 0 / 14%);
}

.chart-container .highcharts-tooltip-box {
    stroke-width: 0;
    border: none;
    stroke: #ebebeb;
    fill: #fff;
    fill-opacity: 1;
}

.chart-container .highcharts-tooltip text {
    fill: #333;
    font-family: "IBM Plex Sans", sans-serif;
}

.chart-container .highcharts-tooltip {
    z-index: 100;
}

.chart-container .highcharts-graph {
    fill: unset;
    stroke-width: unset;
    stroke: unset;
    stroke-linecap: butt;
    stroke-linejoin: round;
}

#maps {
    width: 250px;
    height: 250px;
    border-radius: 4px;
    background-color: #2f2b38;
    border: 1px solid #46465c;
}

#maps .highcharts-background {
    fill: #1f1836;
}

#maps .highcharts-map-series {
    opacity: 0%;
}

#maps .highcharts-plot-background {
    fill: #46445d;
    stroke: transparent;
}

#maps .highcharts-zone-area-0,
#maps .highcharts-zone-area-1 {
    fill: transparent;
    stroke: transparent;
}

#maps .foreground,
#maps .map-point-center .highcharts-color-5 {
    fill: #201836;
    stroke: transparent;
}

#maps .fade-in {
    opacity: 100% !important;
    transition: opacity 1s;
}

#maps .fade {
    opacity: 0% !important;
    transition: opacity 1s;
}

#maps .hide {
    opacity: 0% !important;
    transition: none !important;
}

#maps .show {
    opacity: 100% !important;
    transition: none !important;
}

#maps .transparent {
    fill: transparent;
    stroke: transparent;
}

#maps .green {
    fill: #8bf2b6;
    stroke: #8bf2b6;
}

#maps .highcharts-map-series .highcharts-point {
    stroke: #f0f0f0;
}

#maps .highcharts-map-series .highcharts-point-hover {
    fill-opacity: 1;
}

#maps .highcharts-tooltip-box {
    fill: #fff;
    color: #2f2b38;
    fill-opacity: 1;
    filter: drop-shadow(0 8px 24px rgb(0 0 0 / 20%));
    border-radius: 100px;
}

#maps .highcharts-label-box {
    fill: transparent;
}

#maps .highcharts-tooltip text {
    fill: #2f2b38;
    fill: yellow;
    font-weight: 700;
    font-size: 1em;
}

#maps .highcharts-tooltip {
    stroke: transparent;
    color: #2f2b38;
    font-weight: 700;
    font-size: 0.9em;
}

#maps .dot {
    width: 10px;
    height: 10px;
    border-radius: 20px;
    margin-right: 5px;
    background-color: #1f1836;
    display: inline-block;
}

#maps .highcharts-legend-item:hover text {
    fill: #fff;
}

#maps .highcharts-axis-labels {
    fill: white;
    transform: translateY(-10px);
}

#maps .highcharts-crosshair-thin {
    stroke-width: 10px;
    stroke: #fff;
}

#maps .highcharts-legend-title {
    transform: translate(25px, 2px);
}

#maps .highcharts-zoom-in text,
#maps .highcharts-zoom-out text {
    transform: translateX(4px);
}

#maps .highcharts-title,
#maps .highcharts-subtitle {
    opacity: 0%;
    fill: white;
}

#maps .tip-grid {
    margin-left: 30px;
    display: grid;
    align-items: start;
    grid-template-columns: 75px;
    grid-template-rows: 35px 20px;
}

#maps .tip-content {
    position: absolute;
    left: 25px;
    background-color: #8af2b6;
    border: 0 solid transparent;
    padding: 10px;
    border-radius: 100px;
}

#maps .tip-point {
    font-size: 32px;
    color: #8af2b6;
    margin-top: 17px;
}

@media screen  and (min-width: 300px) {
    #maps {
        width: 300px;
        height: 300px;
    }
}

@media screen  and (min-width: 400px) {
    #maps {
        width: 400px;
        height: 400px;
    }
}

@media screen  and (min-width: 500px) {
    #maps {
        width: 500px;
        height: 500px;
    }
}

