#stock .particle-1.static.fade,
#stock .particle-2.static.fade,
#stock .particle-3.static.fade,
#stock .particle-4.static.fade,
#stock .particle-5.static.fade,
#stock .particle-6.static.fade {
    opacity: 0%;
    transition: opacity 500ms;
}

#stock .particle-1.static {
    transform: translate(70px, -30px);
}

#stock .particle-2.static {
    transform: translate(130px, 0);
}

#stock .particle-3.static {
    transform: translate(180px, -20px);
}

#stock .particle-4.static {
    transform: translate(130px, -40px);
}

#stock .particle-5.static {
    transform: translate(180px, -80px);
}

#stock .particle-6.static {
    transform: translate(180px, 30px);
