Highcharts.chart('container', {

    title: {
        text: 'Different placement of multiple xAxis'
    },

    subtitle: {
        text: 'Using top, left, height and width properties'
    },

    yAxis: [{
        height: '40%'
    }, {
        height: '40%',
        top: '70%',
        offset: 0
    }],

    xAxis: [{
        lineColor: colors[0],
        title: {
            text: 'xAxis 1'
        },
        height: '50%',
        width: '50%'
