js:
// Highcharts 3.0.10, Issue #2339
// Left parameter - inverted chart
Highcharts.chart('container', {

    chart: {
        inverted: true,
        type: 'area'
    },

    xAxis: {
        categories: ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun',
            'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec']
    },

    yAxis: [{
        offset: 0,
        width: 100,
        title: {
            text: 'left: auto'
        }
    }, {
        left: 200,
        width: 100,
        offset: 0,
        title: {
            text: 'left: 200'
        }
    }],

    series: [{
        data: [1, 3, 2, 4],
        name: 'left: auto'
    }, {
        data: [3, 2, 4, 1].reverse(),
        yAxis: 1,
        name: 'left: 200'
    }]

});

css:
#container {
    height: 300px;
    width: 500px;
}

.info {
    font: 0.7em sans-serif;
    padding: 0.5em;
    margin: 0.5em;
    background: #fcffc5;
    border-radius: 0.5em;
    border: 1px solid gray;
}

.info::before {
    content: "i";
    margin: 0.5em;
    font: 1.5em serif bold;
    font-style: italic;
    color: green;
}

