#container {
    height: 500px;
    width: 800px;
    margin: 0 auto;
}

.highcharts-tooltip > span {
    padding: 10px;
    white-space: normal !important;
    width: 200px;
}

.loading {
    margin-top: 10em;
    text-align: center;
    color: gray;
}

.f32 .flag {
    vertical-align: middle !important;
}

