js:
Highcharts.getJSON('https://demo-live-data.highcharts.com/aapl-ohlc.json', function (data) {
    Highcharts.stockChart('container', {
        rangeSelector: {
            selected: 2
        },
        title: {
            text: 'AAPL Stock Price'
        },
        legend: {
            enabled: true
        },
        plotOptions: {
            series: {
                showInLegend: true
            }
        },
        yAxis: [{
            height: '50%'
        }, {
            top: '60%',
            height: '40%'
        }],
        series: [{
            type: 'ohlc',
            id: 'aapl',
            name: 'AAPL Stock Price',
            data: data
        }, {
            type: 'natr',
            linkedTo: 'aapl',
            yAxis: 1
        }, {
            type: 'natr',
            linkedTo: 'aapl',
            yAxis: 1,
            params: {
                period: 50
            }
        }]
    });
});

