            0.75, 0.75, 0.75, 0.75, 0.75, 0.75, 0.75, 0.75, 0.75, 0.75,
            0.75, 0.75, 0.75, 0.75, 0.75, 0.75, 0.75, 0.75, 0.75, 0.75
        ],
        states: {
            hover: {
                halo: {
                    size: 0
                }
            }
        },
        dataLabels: {
            enabled: true,
            formatter: function () {
                return Highcharts.dateFormat('%Y', this.x);
            }
        },
        showInNavigator: false
    }, {
        showInNavigator: true,
        dataLabels: {
            enabled: true,
            align: 'left',
            format: '{point.options.lbl}',
            backgroundColor: '#999999',
            style: {
                width: '300px',
                color: '#FFFFFF',
                textOutline: 'none'
            }
        },
        dataGrouping: {
            enabled: false
        },
        pointWidth: 1,
        threshold: 0.75,
        marker: {
            states: {
                hover: {
                    enabled: false
                }
            }
        },
        type: 'column',
        data: eventsData
    }]
}, function (chart) {

    // Add mousewheel support:
    Highcharts.addEvent(
        chart.container,
        document.onmousewheel === undefined ? 'DOMMouseScroll' : 'mousewheel',
        function (event) {
            var range, delta, extr, step, newMin, newMax,
                axis = chart.xAxis[0],
                prevent = true,
                e;

            e = chart.pointer.normalize(event);
            // Firefox uses e.detail, WebKit and IE uses wheelDelta
            delta = e.detail || -(e.wheelDelta / 120);
            delta = delta < 0 ?
                (axis.reversed ? -1 : 1) :
                (axis.reversed ? 1 : -1);

            if (chart.isInsidePlot(e.chartX - chart.plotLeft, e.chartY - chart.plotTop)) {
                extr = axis.getExtremes();
                range = (extr.max - extr.min);
                step = range * delta / 15;

                newMin = extr.min + step;
                newMax = extr.max + step;

                if (newMax > extr.dataMax) {
                    newMax = extr.dataMax;
                    newMin = newMax - range;
                    prevent = false;
                }
                if (newMin < extr.dataMin) {
                    newMin = extr.dataMin;
                    newMax = newMin + range;
                    prevent = false;
                }

                axis.setExtremes(newMin, newMax, true, false);

                if (prevent) {
                    if (e) {
                        if (e.preventDefault) {
                            e.preventDefault();
                        }
                        if (e.stopPropagation) {
                            e.stopPropagation();
                        }
                        e.cancelBubble = true;
                    }
                }
            }
        }
    );
});

