js:
Highcharts.stockChart('container', {

    chart: {
        type: 'area'
    },

    rangeSelector: {
        selected: 1
    },

    plotOptions: {
        series: {
            gapSize: 1
        }
    },

    series: [{
        name: 'USD to EUR',
        data: usdeur
    }]
});

css:
#container {
    height: 400px;
    min-width: 600px;
}

.info {
    font: 0.7em sans-serif;
    padding: 0.5em;
    margin: 0.5em;
    background: #fcffc5;
    border-radius: 0.5em;
    border: 1px solid gray;
}

.info::before {
    content: "i";
    margin: 0.5em;
    font: 1.5em serif bold;
    font-style: italic;
    color: green;
}

npm:
// 安装
npm install highcharts@11.0.1 --save

// import 模块 
import Highcharts from 'highcharts/highstock';
import Exporting  from 'highcharts/modules/exporting';
import Usdeur     from 'https://www.highcharts.com/samples/data/usdeur.js';

// 初始化模块
Exporting(Highcharts);
Usdeur(Highcharts);
