js:
Highcharts.setOptions({
    lang: {
        // Pre-v9 legacy settings
        rangeSelectorFrom: 'From',
        rangeSelectorTo: 'To'
    }
});

Highcharts.stockChart('container', {

    rangeSelector: {
        buttonTheme: { // styles for the buttons
            fill: 'none',
            stroke: 'none',
            'stroke-width': 0,
            r: 8,
            style: {
                color: '#039',
                fontWeight: 'bold'
            },
            states: {
                hover: {
                },
                select: {
                    fill: '#039',
                    style: {
                        color: 'white'
                    }
                }
                // disabled: { ... }
            }
        },
        inputBoxBorderColor: 'gray',
        inputBoxWidth: 120,
        inputBoxHeight: 18,
        inputStyle: {
            color: '#039',
            fontWeight: 'bold'
        },
        labelStyle: {
            color: 'silver',
            fontWeight: 'bold'
        },
        selected: 1
    },

    series: [{
        name: 'USD to EUR',
        data: usdeur
    }]
});

