// 安装
npm install highcharts@11.0.0 --save

// import 模块 
import Highcharts                         from 'highcharts/highstock';
import Data                               from 'highcharts/modules/data';
import Moment.min                         from 'https://cdnjs.cloudflare.com/ajax/libs/moment.js/2.18.1/moment.min.js';
import MomentTimezoneWithData20122022.min from 'https://cdnjs.cloudflare.com/ajax/libs/moment-timezone/0.5.13/moment-timezone-with-data-2012-2022.min.js';

// 初始化模块
Data(Highcharts);
Moment.min(Highcharts);
MomentTimezoneWithData20122022.min(Highcharts);
