js:
Highcharts.stockChart('container', {

    xAxis: {
        minRange: 30 * 24 * 3600 * 1000
    },

    rangeSelector: {
        selected: 1
    },

    series: [{
        name: 'USD to EUR',
        data: usdeur
    }]
});

css:
#container {
    height: 400px;
    min-width: 600px;
}

.info {
    font: 0.7em sans-serif;
    padding: 0.5em;
    margin: 0.5em;
    background: #fcffc5;
    border-radius: 0.5em;
    border: 1px solid gray;
}

.info::before {
    content: "i";
    margin: 0.5em;
    font: 1.5em serif bold;
    font-style: italic;
    color: green;
}

npm:
// 安装
npm install highcharts@10.3.2 --save

// import 模块 
import Highcharts      from 'highcharts/highstock';
import Exporting       from 'highcharts/modules/exporting';
import Usdeur          from 'https://www.highcharts.com/samples/data/usdeur.js';
import HighchartsZh_CN from 'https://code.hcharts.cn/plugins/highcharts-zh_CN.js';

// 初始化模块
Exporting(Highcharts);
Usdeur(Highcharts);
HighchartsZh_CN(Highcharts);
